import React, { useEffect, useState } from 'react';
import { useCart } from './CartContext';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, getDoc, updateDoc } from 'firebase/firestore';
import { app } from './firebase';

const db = getFirestore(app);
const auth = getAuth(app);

const Success: React.FC = () => {
  const { clearCart } = useCart();
  // 購入商品・合計金額をlocalStorageから取得
  const [orderItems, setOrderItems] = useState<any[]>([]);
  const [paidAmount, setPaidAmount] = useState<number>(0);
  const [usePoint, setUsePoint] = useState<number>(0);

  useEffect(() => {
    // Stripeから直接情報を受け取る場合はここでAPIコール等を実装（今後拡張）
    // 現状はlocalStorageから取得
    try {
      const itemsStr = localStorage.getItem('mcSquareLastOrderItems');
      if (itemsStr) setOrderItems(JSON.parse(itemsStr));
      const paidAmountStr = localStorage.getItem('mcSquareLastPaidAmount');
      if (paidAmountStr) setPaidAmount(parseInt(paidAmountStr, 10));
      const usePointStr = localStorage.getItem('mcSquareUsePoint');
      if (usePointStr) setUsePoint(parseInt(usePointStr, 10));
    } catch {}
  }, []);

  useEffect(() => {
    // ポイント付与・履歴追加フラグをリセット（決済ごとに必ずリセット）
    localStorage.removeItem('mcSquarePointGranted');
    // ポイント付与・履歴追加が既に実行されたか判定
    if (localStorage.getItem('mcSquarePointGranted') === '1') {
      clearCart();
      localStorage.removeItem('mcSquareLastPaidAmount');
      return;
    }
    clearCart();
    // 決済金額からポイント付与・購入履歴追加
    const grantPointsAndSaveOrder = async (user: any) => {
      if (!user) return;
      try {
        const userRef = doc(db, 'users', user.uid);
        const userSnap = await getDoc(userRef);
        if (!userSnap.exists()) return;
        // Firestoreの現在ポイント
        let prevPoint = userSnap.data().point || 0;
        // ポイント減算（使った分を引く）
        if (usePoint > 0) {
          prevPoint = Math.max(0, prevPoint - usePoint);
          await updateDoc(userRef, { point: prevPoint });
          console.log('ポイント使用:', usePoint, '残:', prevPoint);
        }
        // ポイント付与
        if (paidAmount > 0) {
          const addPoint = Math.floor(paidAmount / 100);
          await updateDoc(userRef, { point: prevPoint + addPoint });
          console.log('ポイント付与:', addPoint, '合計:', prevPoint + addPoint);
          prevPoint = prevPoint + addPoint;
        }
        // 購入履歴追加
        if (orderItems.length > 0) {
          const prevOrders = userSnap.data().orders || [];
          const now = new Date();
          const orderRecord = {
            date: now.toISOString(),
            items: orderItems,
            total: paidAmount,
            usedPoint: usePoint
          };
          await updateDoc(userRef, { orders: [...prevOrders, orderRecord] });
          console.log('購入履歴追加:', orderRecord);
        }
        // ポイント付与・履歴追加後はlocalStorageをクリア
        localStorage.removeItem('mcSquareLastPaidAmount');
        localStorage.removeItem('mcSquareUsePoint');
        localStorage.removeItem('mcSquareLastOrderItems');
        localStorage.setItem('mcSquarePointGranted', '1'); // フラグを立てる
      } catch (e) {
        console.error('ポイント付与・履歴追加エラー:', e);
      }
    };
    // 認証状態が復帰するまでしっかり待つ
    if (auth.currentUser) {
      grantPointsAndSaveOrder(auth.currentUser);
    } else {
      const unsubscribe = onAuthStateChanged(auth, user => {
        if (user) {
          grantPointsAndSaveOrder(user);
          unsubscribe();
        }
      });
    }
  }, [clearCart, orderItems, paidAmount, usePoint]);

  return (
    <div style={{ padding: '3em 1em', textAlign: 'center' }}>
      <h1 style={{ color: '#E1306C', marginBottom: '1em' }}>ご購入ありがとうございました！</h1>
      <p>ご注文が正常に完了しました。<br />ご登録のメールアドレスに確認メールをお送りしています。</p>
      {/* 購入商品リスト表示 */}
      {orderItems.length > 0 && (
        <div style={{ margin: '2em auto', maxWidth: 480, background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(255,159,124,0.10)', padding: '2em' }}>
          <h3 style={{ color: '#E1306C', marginBottom: 16, fontSize: '1.15em' }}>ご購入商品</h3>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {orderItems.map((item, idx) => (
              <li key={idx} style={{ display: 'flex', alignItems: 'center', gap: 14, marginBottom: 18, borderBottom: '1px solid #eee', paddingBottom: 10 }}>
                <img src={item.imageUrl || '/Image/MC square Logo.png'} alt={item.name} style={{ width: 48, height: 48, objectFit: 'cover', borderRadius: 8, border: '1px solid #eee', background: '#fafafa' }} />
                <div style={{ flex: 1, textAlign: 'left' }}>
                  <div style={{ fontWeight: 700 }}>{item.name}</div>
                  <div style={{ color: '#636E72', fontSize: '0.97em' }}>数量: {item.quantity}　単価: {Number(item.price).toLocaleString()}円</div>
                </div>
              </li>
            ))}
          </ul>
          <div style={{ fontWeight: 700, fontSize: '1.1em', color: 'var(--color-primary)', textAlign: 'right', marginTop: 18 }}>
            合計: {paidAmount.toLocaleString()}円
          </div>
        </div>
      )}
      <a href="/all-products" style={{ display: 'inline-block', marginTop: '2em', color: '#fff', background: '#E1306C', padding: '0.8em 2em', borderRadius: 8, textDecoration: 'none', fontWeight: 700 }}>商品一覧へ戻る</a>
    </div>
  );
};

export default Success;
