import React from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Header from './Header';
import { useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import Footer from './Footer';
import { CartProvider } from './CartContext';

// FirebaseË®≠ÂÆö
const firebaseConfig = {
  apiKey: "AIzaSyB2nPjePLfr6ZlxTOLPC7OuYWrD0k6JqJ4",
  authDomain: "link-manager-f4ea8.firebaseapp.com",
  projectId: "link-manager-f4ea8",
  storageBucket: "link-manager-f4ea8.firebasestorage.app",
  messagingSenderId: "96350140236",
  appId: "1:96350140236:web:fe858a8661067f5f0081e9",
  measurementId: "G-90CXT9WS4Y"
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

function NewsSection() {
  const [news, setNews] = useState<{id: string, date: string, title: string, content: string}[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const newsCol = collection(db, 'news');
        const snapshot = await getDocs(newsCol);
        const newsList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })) as any[];
        // Êó•‰ªò„ÅßÈôçÈ†Ü„ÇΩ„Éº„Éà
        newsList.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
        setNews(newsList);
      } catch (e) {
        setError('„ÅäÁü•„Çâ„Åõ„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <section className="news-section">
      <div className="news-title-section">„ÅäÁü•„Çâ„Åõ„ÉªÊúÄÊñ∞ÊÉÖÂ†±</div>
      {loading && <div>Ë™≠„ÅøËæº„Åø‰∏≠...</div>}
      {error && <div style={{color: 'red'}}>{error}</div>}
      {news.map(item => (
        <div className="news-item" key={item.id}>
          <div className="news-date">{item.date}</div>
          <div className="news-title">{item.title}</div>
          <div className="news-content show">{item.content}</div>
        </div>
      ))}
      {(!loading && news.length === 0) && <div>„ÅäÁü•„Çâ„Åõ„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì</div>}
    </section>
  );
}

function App() {
  return (
    <div className="container">
      <Header cartCount={0} />
      <main>
        <section className="hero-section">
          <h1 className="hero-title">„ÅÑ„ÅÑ„ÇÇ„ÅÆ„ÅØ„ÅÑ„Å§„Åæ„Åß„ÇÇ<br />„Ç®„É†„Ç∑„Éº„Çπ„ÇØ„Ç®„Ç¢ÂÖ¨Âºè„Çµ„Ç§„Éà</h1>
          <p className="hero-subtitle">ÁîüÂú∞„ÅÆË≤©Â£≤„Éª„Ç§„É≥„Éï„É´„Ç®„É≥„Çµ„Éº„Ç≥„É©„Éú„Éª„Çà„Çä„Åù„ÅÑ„ÇØ„É©„Éï„Éà„Å™„Å©„ÄÅ<br />„ÅÇ„Å™„Åü„ÅÆÊâã‰Ωú„Çä„ÇíÂøúÊè¥„Åô„ÇãÊú¨Ê†ºEC„Çµ„Ç§„Éà„Å∏„Çà„ÅÜ„Åì„Åù„ÄÇ</p>
          <div className="hero-buttons">
            <Link to="/all-products" className="hero-btn">
              <span role="img" aria-label="ÁîüÂú∞">üßµ</span>
              ÁîüÂú∞„ÅÆË≤©Â£≤
            </Link>
            <Link to="/subscription" className="hero-btn">
              <span role="img" aria-label="„Çµ„Éñ„Çπ„ÇØ">üì¶</span>
              „Çµ„Éñ„Çπ„ÇØ
            </Link>
            <Link to="/influencer_subscription" className="hero-btn">
              <span role="img" aria-label="„Ç≥„É©„Éú">üåü</span>
              „Ç§„É≥„Éï„É´„Ç®„É≥„Çµ„Éº„Ç≥„É©„Éú
            </Link>
            <Link to="/patchwork_kit_website" className="hero-btn">
              <span role="img" aria-label="„ÇØ„É©„Éï„Éà">‚ú®</span>
              „Çà„Çä„Åù„ÅÑ„ÇØ„É©„Éï„Éà
            </Link>
            <Link to="/kits" className="hero-btn">
              <span role="img" aria-label="„Ç≠„ÉÉ„Éà">üß©</span>
              „Ç≠„ÉÉ„Éà
            </Link>
          </div>
        </section>
        <NewsSection />
