import React, { useState } from 'react';
import { useCart } from './CartContext';

const getImageSrc = (url?: string) => {
  if (!url) return '/Image/MC square Logo.png';
  if (url.startsWith('http://') || url.startsWith('https://')) return url;
  if (url.startsWith('/Image/')) return url;
  if (url.startsWith('Image/')) return '/' + url;
  return '/Image/MC square Logo.png';
};

const Cart: React.FC = () => {
  const { cart, cartCount, totalPrice, updateQuantity, removeFromCart } = useCart();
  const [open, setOpen] = useState(false);

  // „Ç´„Éº„Éà„Éú„Çø„É≥ÔºàÂ∏∏ÊôÇË°®Á§∫Ôºâ
  return (
    <div style={{ position: 'fixed', top: 20, right: 20, zIndex: 4000 }}>
      <button
        onClick={() => setOpen(o => !o)}
        style={{
          background: 'var(--color-primary)',
          color: '#fff',
          border: 'none',
          borderRadius: 24,
          padding: '0.6em 1.3em',
          fontWeight: 700,
          fontSize: '1.08em',
          boxShadow: '0 2px 8px rgba(255,159,124,0.10)',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: 8,
          position: 'relative',
        }}
        aria-label="„Ç´„Éº„Éà„ÇíÈñã„Åè"
      >
        <span role="img" aria-label="cart">üõí</span>
        „Ç´„Éº„Éà
        <span style={{ background: '#fff', color: 'var(--color-primary)', borderRadius: '50%', width: 24, height: 24, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 700, fontSize: '0.98em', marginLeft: 6 }}>{cartCount}</span>
      </button>
      {open && cart.length > 0 && (
        <div style={{ position: 'absolute', top: 48, right: 0, background: '#fff', border: '1px solid #FFD4C4', borderRadius: 12, boxShadow: '0 2px 16px rgba(255,159,124,0.18)', padding: '1em 1.5em', zIndex: 4100, minWidth: 320, maxWidth: 400 }}>
          <div style={{ fontWeight: 700, color: 'var(--color-primary)', marginBottom: 8, fontSize: '1.1em' }}>üõí „Ç´„Éº„Éà</div>
          <div style={{ marginBottom: 8 }}>ÂïÜÂìÅÊï∞: <b>{cartCount}</b></div>
          <div style={{ marginBottom: 12 }}>ÂêàË®à: <b>{totalPrice.toLocaleString()}ÂÜÜ</b></div>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0, maxHeight: 220, overflowY: 'auto' }}>
            {cart.map(item => (
              <li key={item.managementNumber} style={{ display: 'flex', alignItems: 'center', gap: '0.7em', borderBottom: '1px solid #FFD4C4', padding: '0.7em 0' }}>
                <img src={getImageSrc(item.imageUrl)} alt={item.name} style={{ width: 48, height: 48, objectFit: 'cover', borderRadius: 8, border: '1px solid #FFD4C4', background: '#fafafa' }} />
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 700, fontSize: '1em' }}>{item.name}</div>
                  <div style={{ color: '#636E72', fontSize: '0.95em' }}>Âçò‰æ°: {Number(String(item.price).replace(/[^\d.]/g, '')).toLocaleString()}ÂÜÜ</div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5em', marginTop: 4 }}>
                    <button onClick={() => updateQuantity(item.managementNumber, Math.max(1, item.quantity - 1))} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>-</button>
                    <span style={{ fontWeight: 600, minWidth: 32, textAlign: 'center' }}>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.managementNumber, item.quantity + 1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>+</button>
                  </div>
                </div>
                <button onClick={() => removeFromCart(item.managementNumber)} style={{ background: '#ff6b6b', color: '#fff', border: 'none', borderRadius: 6, padding: '0.3em 0.7em', fontSize: '0.95em', cursor: 'pointer', marginLeft: 4 }}>ÂâäÈô§</button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {open && cart.length === 0 && (
        <div style={{ position: 'absolute', top: 48, right: 0, background: '#fff', border: '1px solid #FFD4C4', borderRadius: 12, boxShadow: '0 2px 16px rgba(255,159,124,0.18)', padding: '1.2em 1.5em', zIndex: 4100, minWidth: 320, maxWidth: 400, textAlign: 'center', color: '#888' }}>
          „Ç´„Éº„Éà„Å´ÂïÜÂìÅ„Åå„ÅÇ„Çä„Åæ„Åõ„Çì
        </div>
      )}
    </div>
  );
};

export default Cart;
