import React, { useEffect, useState } from 'react';
// Firebase
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { useCart } from './CartContext';

const firebaseConfig = {
  apiKey: "AIzaSyB2nPjePLfr6ZlxTOLPC7OuYWrD0k6JqJ4",
  authDomain: "link-manager-f4ea8.firebaseapp.com",
  projectId: "link-manager-f4ea8",
  storageBucket: "link-manager-f4ea8.firebasestorage.app",
  messagingSenderId: "96350140236",
  appId: "1:96350140236:web:fe858a8661067f5f0081e9",
  measurementId: "G-90CXT9WS4Y"
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

interface LinkData {
  id: string;
  title: string;
  price?: string;
  applyUrl?: string;
  influencerUrl?: string;
  imageUrl?: string;
}

interface CartItem {
  managementNumber: string;
  name: string;
  price: string;
  imageUrl?: string;
  quantity: number;
  length?: number;
}

const InfluencerCollab: React.FC = () => {
  const [links, setLinks] = useState<LinkData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [cartModalOpen, setCartModalOpen] = useState(false);

  // „Ç∞„É≠„Éº„Éê„É´„Ç´„Éº„Éà„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà„Çí‰ΩøÁî®
  const { cart, addToCart, removeFromCart, updateQuantity, cartCount, totalPrice } = useCart();

  // Firestore„Åã„Çâ„Ç≥„É©„ÉúÂïÜÂìÅ„É™„É≥„ÇØÂèñÂæó
  useEffect(() => {
    async function fetchLinks() {
      setLoading(true);
      setError(null);
      try {
        const linksCol = collection(db, 'links');
        const snapshot = await getDocs(linksCol);
        const linksList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as LinkData));
        setLinks(linksList);
      } catch (e: any) {
        setError('„É™„É≥„ÇØ„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
        console.error('Error fetching links:', e);
      } finally {
        setLoading(false);
      }
    }
    fetchLinks();
  }, []);

  // „Ç´„Éº„ÉàÊìç‰Ωú
  const handleChangeQuantity = (managementNumber: string, diff: number) => {
    const currentItem = cart.find(item => item.managementNumber === managementNumber);
    if (currentItem) {
      const newQuantity = Math.max(1, currentItem.quantity + diff);
      updateQuantity(managementNumber, newQuantity);
    }
  };
  const handleRemove = (managementNumber: string) => {
    removeFromCart(managementNumber);
  };
  const handleAddToCart = (product: any) => {
    addToCart(product);
  };
  // Ê±∫Ê∏à„Éú„Çø„É≥„ÅØAllProductsÁ≠â„Å®Âêå„Åò„Åè„ÄÅHeader„ÅÆ„Ç´„Éº„ÉàUI„Åã„ÇâË°å„ÅÜÊÉ≥ÂÆö

  // ÁîªÂÉè„Éë„ÇπÊ≠£Ë¶èÂåñÈñ¢Êï∞ÔºàÊú™‰ΩøÁî®ÂåñÔºâ
  // const getImageSrc = (url?: string) => {
  //   if (!url) return '/Image/MC square Logo.png';
  //   if (url.startsWith('http://') || url.startsWith('https://')) return url;
  //   if (url.startsWith('/Image/')) return url;
  //   if (url.startsWith('Image/')) return '/' + url;
  //   return '/Image/MC square Logo.png';
  // };

  // „Ç´„Éº„Éà„É¢„Éº„ÉÄ„É´
  const CartModal = () => (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', zIndex: 2000, display: 'flex', alignItems: 'center', justifyContent: 'center' }} onClick={() => setCartModalOpen(false)}>
      <div style={{ background: '#fff', borderRadius: 16, padding: '2em', minWidth: 320, maxWidth: 400, boxShadow: '0 8px 32px rgba(0,0,0,0.15)', position: 'relative' }} onClick={e => e.stopPropagation()}>
        <h2 style={{ fontSize: '1.2em', color: 'var(--color-primary)', marginBottom: '1em' }}>„Ç´„Éº„Éà„ÅÆ‰∏≠Ë∫´</h2>
        {cart.length === 0 ? (
          <div style={{ color: '#888', textAlign: 'center', margin: '2em 0' }}>„Ç´„Éº„Éà„Å´ÂïÜÂìÅ„Åå„ÅÇ„Çä„Åæ„Åõ„Çì</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {cart.map(item => (
              <li key={item.managementNumber} style={{ display: 'flex', alignItems: 'center', gap: '1em', borderBottom: '1px solid #FFD4C4', padding: '0.7em 0' }}>
                <img src={item.imageUrl} alt={item.name} style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 8, border: '1px solid #FFD4C4' }} />
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 700 }}>{item.name}</div>
                  <div style={{ color: '#636E72', fontSize: '0.95em' }}>{item.price} √ó {item.quantity}Êûö</div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5em', marginTop: 4 }}>
                    <button onClick={() => handleChangeQuantity(item.managementNumber, -1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>-</button>
                    <span style={{ fontWeight: 600, minWidth: 32, textAlign: 'center' }}>{item.quantity}Êûö</span>
                    <button onClick={() => handleChangeQuantity(item.managementNumber, 1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>+</button>
                  </div>
                </div>
                <button onClick={() => handleRemove(item.managementNumber)} style={{ background: '#ff6b6b', color: '#fff', border: 'none', borderRadius: 6, padding: '0.3em 0.6em', fontSize: '0.9em', cursor: 'pointer' }}>ÂâäÈô§</button>
              </li>
            ))}
          </ul>
        )}
        <div style={{ marginTop: '1.5em', fontWeight: 700, fontSize: '1.1em', textAlign: 'right' }}>ÂêàË®à: {totalPrice.toLocaleString()}ÂÜÜ</div>
        <button onClick={() => setCartModalOpen(false)} style={{ marginTop: '1em', background: '#ccc', color: '#333', border: 'none', borderRadius: 8, padding: '0.7em 1.5em', fontWeight: 700, fontSize: '1em', cursor: 'pointer', display: 'block', width: '100%' }}>Èñâ„Åò„Çã</button>
      </div>
    </div>
  );

  return (
    <div className="container">
      {cartModalOpen && <CartModal />}
      <h1 style={{ color: 'var(--color-primary)', fontSize: '1.6rem', marginBottom: '0.5rem' }}>„Ç§„É≥„Éï„É´„Ç®„É≥„Çµ„Éº„Ç≥„É©„ÉúÂïÜÂìÅ</h1>
