import React, { useEffect, useState } from 'react';
import { getAuth } from 'firebase/auth';
import { getFirestore, doc, getDoc, setDoc, updateDoc } from 'firebase/firestore';
import { app } from './firebase';

const db = getFirestore(app);
const auth = getAuth(app);

const MyPage: React.FC = () => {
  const [userData, setUserData] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [membershipNumber, setMembershipNumber] = useState('');
  const [membershipMatch, setMembershipMatch] = useState<boolean | null>(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [registerName, setRegisterName] = useState('');
  const [registerMembership, setRegisterMembership] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      setError(null);
      try {
        const user = auth.currentUser;
        if (!user) {
          setError('ログインしてください');
          setLoading(false);
          return;
        }
        const userRef = doc(db, 'users', user.uid);
        const userSnap = await getDoc(userRef);
        if (userSnap.exists()) {
          setUserData(userSnap.data());
          setShowRegisterForm(false);
        } else {
          setShowRegisterForm(true);
        }
      } catch (e: any) {
        setError('データ取得エラー: ' + e.message);
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, []);

  const handleMembershipCheck = () => {
    if (!userData) return;
    setMembershipMatch(userData.membershipNumber === membershipNumber);
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    try {
      const user = auth.currentUser;
      if (!user) {
        setError('ログインしてください');
        setLoading(false);
        return;
      }
      const userRef = doc(db, 'users', user.uid);
      await setDoc(userRef, {
        name: registerName,
        email: user.email,
        membershipNumber: registerMembership,
        point: 0,
        orders: []
      });
      setUserData({
        name: registerName,
        email: user.email,
        membershipNumber: registerMembership,
        point: 0,
        orders: []
      });
      setShowRegisterForm(false);
    } catch (e: any) {
      setError('登録エラー: ' + e.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div style={{ padding: '2em' }}>読み込み中...</div>;
  if (error) return <div style={{ color: 'red', padding: '2em' }}>{error}</div>;

  if (showRegisterForm) {
    return (
      <div style={{ maxWidth: 500, margin: '2em auto', background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(255,159,124,0.10)', padding: '2em' }}>
        <h2 style={{ color: 'var(--color-primary)', marginBottom: 24 }}>マイページ初回登録</h2>
        <form onSubmit={handleRegister}>
          <div style={{ marginBottom: 16 }}>
            <label>名前：</label><br />
            <input type="text" value={registerName} onChange={e => setRegisterName(e.target.value)} required style={{ width: '100%', padding: '0.7em', borderRadius: 8, border: '1px solid #ccc' }} />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>会員番号：</label><br />
            <input type="text" value={registerMembership} onChange={e => setRegisterMembership(e.target.value)} required style={{ width: '100%', padding: '0.7em', borderRadius: 8, border: '1px solid #ccc' }} />
          </div>
          <button type="submit" style={{ width: '100%', padding: '0.8em', borderRadius: 8, background: 'var(--color-primary)', color: '#fff', border: 'none', fontWeight: 700, fontSize: '1.08em' }}>登録</button>
        </form>
      </div>
    );
  }

  if (!userData) return null;

  return (
    <div style={{ maxWidth: 500, margin: '2em auto', background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(255,159,124,0.10)', padding: '2em' }}>
      <h2 style={{ color: 'var(--color-primary)', marginBottom: 24 }}>マイページ</h2>
      <div style={{ marginBottom: 16 }}><b>名前:</b> {userData.name || '-'}</div>
      <div style={{ marginBottom: 16 }}><b>メールアドレス:</b> {userData.email || '-'}</div>
      <div style={{ marginBottom: 16 }}><b>会員番号:</b> {userData.membershipNumber || '-'}</div>
      <div style={{ marginBottom: 16 }}><b>現在のポイント:</b> {userData.point !== undefined ? userData.point : '-'} pt</div>
      <div style={{ marginBottom: 24 }}>
        <b>購入履歴:</b>
        <ul style={{ margin: 0, paddingLeft: 20 }}>
          {userData.orders && userData.orders.length > 0 ? (
            userData.orders.map((order: any, idx: number) => (
              <li key={idx}>{order}</li>
            ))
          ) : (
            <li>購入履歴はありません</li>
          )}
        </ul>
      </div>
      <div style={{ marginBottom: 16 }}>
        <b>会員番号の照合:</b><br />
        <input
          type="text"
          value={membershipNumber}
          onChange={e => setMembershipNumber(e.target.value)}
          placeholder="会員番号を入力"
          style={{ padding: '0.5em', borderRadius: 8, border: '1px solid #ccc', marginRight: 8 }}
        />
        <button onClick={handleMembershipCheck} style={{ padding: '0.5em 1.2em', borderRadius: 8, background: 'var(--color-primary)', color: '#fff', border: 'none', fontWeight: 700 }}>照合</button>
        {membershipMatch === true && <span style={{ color: 'green', marginLeft: 12 }}>✔ 一致しました</span>}
        {membershipMatch === false && <span style={{ color: 'red', marginLeft: 12 }}>✖ 一致しません</span>}
      </div>
    </div>
  );
};

export default MyPage;
