import React from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';

// Firebase設定
const firebaseConfig = {
  apiKey: "AIzaSyB2nPjePLfr6ZlxTOLPC7OuYWrD0k6JqJ4",
  authDomain: "link-manager-f4ea8.firebaseapp.com",
  projectId: "link-manager-f4ea8",
  storageBucket: "link-manager-f4ea8.firebasestorage.app",
  messagingSenderId: "96350140236",
  appId: "1:96350140236:web:fe858a8661067f5f0081e9",
  measurementId: "G-90CXT9WS4Y"
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

function NewsSection() {
  const [news, setNews] = useState<{id: string, date: string, title: string, content: string}[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const newsCol = collection(db, 'news');
        const snapshot = await getDocs(newsCol);
        const newsList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })) as any[];
        // 日付で降順ソート
        newsList.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
        setNews(newsList);
      } catch (e) {
        setError('お知らせの取得に失敗しました');
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <section className="news-section">
      <div className="news-title-section">お知らせ・最新情報</div>
      {loading && <div>読み込み中...</div>}
      {error && <div style={{color: 'red'}}>{error}</div>}
      {news.map(item => (
        <div className="news-item" key={item.id}>
          <div className="news-date">{item.date}</div>
          <div className="news-title">{item.title}</div>
          <div className="news-content show">{item.content}</div>
        </div>
      ))}
      {(!loading && news.length === 0) && <div>お知らせはありません</div>}
    </section>
  );
}

function App() {
  return (
    <div className="container">
      <header style={{background: 'var(--color-background-alt)', borderBottom: '1px solid var(--color-border)', position: 'sticky', top: 0, zIndex: 100, padding: 0, margin: 0}}>
        <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', maxWidth: 1200, margin: '0 auto', padding: '0.6em 1.2em'}}>
          <div style={{display: 'flex', alignItems: 'center', gap: '0.7em', minWidth: 120}}>
            <Link to="/">
              <img src="/Image/MC square Logo.png" alt="MC Square ロゴ" style={{height: 38, width: 'auto', display: 'block'}} />
            </Link>
            <Link to="/" style={{textDecoration: 'none'}}>
              <span style={{fontSize: '1.3rem', fontWeight: 700, color: 'var(--color-primary)', letterSpacing: '0.04em'}}>エムシースクエア</span>
            </Link>
          </div>
          {/* 新しい画像を追加 */}
          {/* <div style={{display: 'flex', alignItems: 'center', gap: '1em'}}>
            <img 
              src="/Image/JP Cotton subscription.png" 
              alt="JP Cotton subscription" 
              style={{height: 50, width: 'auto', borderRadius: '8px'}} 
            />
            <img 
              src="/Image/US Cotton subscription.png" 
              alt="US Cotton subscription" 
              style={{height: 50, width: 'auto', borderRadius: '8px'}} 
            />
          </div> */}
          <div style={{display: 'flex', alignItems: 'center', gap: '1.2em', minWidth: 180, justifyContent: 'flex-end'}}>
            <a href="#" title="マイページ" style={{color: 'var(--color-text)', fontSize: '1.4em', textDecoration: 'none', display: 'flex', alignItems: 'center', gap: '0.2em'}}><span role="img" aria-label="mypage">👤</span><span style={{fontSize: '0.9em'}}>マイページ</span></a>
            <a href="#" title="カート" style={{color: 'var(--color-text)', fontSize: '1.4em', textDecoration: 'none', display: 'flex', alignItems: 'center', position: 'relative', gap: '0.2em'}}><span role="img" aria-label="cart">🛒</span><span style={{fontSize: '0.9em'}}>カート</span><span id="cartCount" style={{position: 'absolute', top: -8, right: -18, background: 'var(--color-secondary)', color: '#fff', borderRadius: '50%', width: 20, height: 20, display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '0.8em', fontWeight: 'bold'}}>0</span></a>
          </div>
        </div>
        <nav style={{background: '#f7f3ef', borderTop: '1px solid var(--color-border)', borderBottom: '1px solid var(--color-border)', margin: 0, padding: 0}}>
          <ul style={{display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '2.5em', margin: 0, padding: '0.5em 0', listStyle: 'none', fontSize: '1.08em'}}>
            <li><Link to="/all-products" style={{color: 'var(--color-text)', textDecoration: 'none', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '0.2em'}}><span style={{fontSize: '1.3em'}}>🧵</span><span style={{fontSize: '0.98em'}}>生地</span></Link></li>
