import React, { useEffect, useState } from 'react';
import Header from './Header';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';

const firebaseConfig = {
  apiKey: "AIzaSyB2nPjePLfr6ZlxTOLPC7OuYWrD0k6JqJ4",
  authDomain: "link-manager-f4ea8.firebaseapp.com",
  projectId: "link-manager-f4ea8",
  storageBucket: "link-manager-f4ea8.firebasestorage.app",
  messagingSenderId: "96350140236",
  appId: "1:96350140236:web:fe858a8661067f5f0081e9",
  measurementId: "G-90CXT9WS4Y"
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

interface LinkData {
  id: string;
  title: string;
  price?: string;
  applyUrl?: string;
  influencerUrl?: string;
  imageUrl?: string;
}

const Subscription: React.FC = () => {
  const [links, setLinks] = useState<LinkData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchLinks() {
      setLoading(true);
      setError(null);
      try {
        const linksCol = collection(db, 'links');
        const snapshot = await getDocs(linksCol);
        // サブスク商品だけを抽出（titleやpriceなどで判定。ここでは全件表示）
        const linksList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as LinkData));
        setLinks(linksList);
      } catch (e: any) {
        setError('リンクの取得に失敗しました');
        console.error('Error fetching links:', e);
      } finally {
        setLoading(false);
      }
    }
    fetchLinks();
  }, []);

  return (
    <div className="container">
      <Header cartCount={0} />
      <h1 style={{ color: 'var(--color-primary)', fontSize: '1.6rem', marginBottom: '0.5rem' }}>サブスクリプション商品一覧</h1>
      {loading && <div>データを読み込み中...</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <div className="inventory-list" style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem', justifyContent: 'center', marginTop: '2rem' }}>
        {links.map(link => (
          <div key={link.id} className="inventory-card" style={{ background: '#fff', borderRadius: 20, boxShadow: '0 4px 16px rgba(255, 159, 124, 0.10)', border: '1px solid #FFD4C4', width: 320, padding: '1.5rem', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            {link.imageUrl && <img src={link.imageUrl} alt={link.title} style={{ width: 160, height: 160, objectFit: 'cover', borderRadius: 16, border: '1px solid #FFD4C4', marginBottom: '1rem', background: '#f8f8f8' }} />}
            <div className="product-name" style={{ fontWeight: 700, fontSize: '1.1em', marginBottom: '0.3em' }}>{link.title}</div>
            <div className="price" style={{ color: 'var(--color-primary)', fontSize: '1.25em', fontWeight: 700, marginBottom: '0.7em' }}>{link.price}</div>
            <div style={{ display: 'flex', gap: '1em', marginTop: '0.7em' }}>
              {link.applyUrl && <a href={link.applyUrl} target="_blank" rel="noopener" style={{ background: 'var(--color-primary)', color: '#fff', textDecoration: 'none', padding: '0.6rem 1.3rem', borderRadius: 20, fontWeight: 500, fontSize: '1rem', boxShadow: '0 2px 8px rgba(255, 159, 124, 0.08)' }}>申し込みページへ</a>}
              {link.influencerUrl && <a href={link.influencerUrl} target="_blank" rel="noopener" style={{ background: '#fff', color: 'var(--color-primary)', border: '2px solid var(--color-primary)', fontWeight: 700, padding: '0.6rem 1.3rem', borderRadius: 20, fontSize: '1rem' }}>インフルエンサーを見る</a>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Subscription;
