import React, { useEffect, useState, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useCart } from './CartContext';

const navItems = [
  { to: '/all-products', icon: '🧵', label: '生地' },
  { to: '/kits', icon: '🧩', label: 'キット' },
  { to: '/influencer_subscription', icon: '🌟', label: 'コラボ' },
  { to: '/subscription', icon: '📦', label: 'サブスク' },
  { to: '/patchwork_kit_website', icon: '✨', label: 'よりそいクラフト' },
];

const getImageSrc = (url?: string) => {
  if (!url) return '/Image/MC square Logo.png';
  if (url.startsWith('http://') || url.startsWith('https://')) return url;
  if (url.startsWith('/Image/')) return url;
  if (url.startsWith('Image/')) return '/' + url;
  return '/Image/MC square Logo.png';
};

const Header: React.FC = () => {
  const location = useLocation();
  const { cart, cartCount, totalPrice, updateQuantity, removeFromCart } = useCart();
  const [cartOpen, setCartOpen] = useState(false);
  const cartRef = useRef<HTMLDivElement>(null);

  // カート詳細の外側クリックで閉じる
  useEffect(() => {
    if (!cartOpen) return;
    const handleClick = (e: MouseEvent) => {
      if (cartRef.current && !cartRef.current.contains(e.target as Node)) {
        setCartOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [cartOpen]);

  // レスポンシブ用のstyleをheadに追加
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      @media (max-width: 600px) {
        .header-nav-list {
          gap: 1.1em !important;
          font-size: 0.92em !important;
        }
        .header-nav-link-icon {
          font-size: 1.08em !important;
        }
        .header-nav-link-label {
          font-size: 0.90em !important;
        }
        .header-main-row {
          padding: 0.4em 0.5em !important;
        }
      }
    `;
    document.head.appendChild(style);
    return () => { document.head.removeChild(style); };
  }, []);

  return (
    <header style={{ background: 'var(--color-background-alt)', borderBottom: '1px solid var(--color-border)', position: 'sticky', top: 0, zIndex: 100 }}>
      <div className="header-main-row" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', maxWidth: 1200, margin: '0 auto', padding: '0.6em 1.2em', position: 'relative' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '0.7em', minWidth: 120 }}>
          <Link to="/">
            <img src="/Image/MC square Logo.png" alt="MC Square ロゴ" style={{ height: 34, width: 'auto', display: 'block' }} />
          </Link>
          <Link to="/" style={{ textDecoration: 'none' }}>
            <span style={{ fontSize: '1.08rem', fontWeight: 700, color: 'var(--color-primary)', letterSpacing: '0.04em' }}>エムシースクエア</span>
          </Link>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '0.7em', minWidth: 120, justifyContent: 'flex-end', position: 'relative' }}>
          <Link to="#" style={{ color: 'var(--color-primary)', fontSize: '1em', textDecoration: 'none', display: 'flex', alignItems: 'center', gap: '0.2em' }}><span role="img" aria-label="mypage">👤</span><span style={{ fontSize: '0.92em' }}>マイページ</span></Link>
          <button
            onClick={() => setCartOpen(o => !o)}
            style={{
              background: 'var(--color-primary)',
              color: '#fff',
              border: 'none',
              borderRadius: 24,
              padding: '0.5em 1.2em',
              fontWeight: 700,
              fontSize: '1.05em',
              boxShadow: '0 2px 8px rgba(255,159,124,0.10)',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: 8,
              position: 'relative',
              transition: 'background 0.2s',
            }}
            aria-label="カートを開く"
          >
            <span role="img" aria-label="cart">🛒</span>
            カート
            <span style={{ background: '#fff', color: 'var(--color-primary)', borderRadius: '50%', width: 22, height: 22, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 700, fontSize: '0.98em', marginLeft: 6 }}>{cartCount}</span>
          </button>
          {cartOpen && (
            <div ref={cartRef} style={{ position: 'absolute', top: 56, right: 0, background: '#fff', border: '2px solid #FFD4C4', borderRadius: 18, boxShadow: '0 8px 32px rgba(255,159,124,0.18)', padding: '2em 2em 1.5em 2em', zIndex: 4100, minWidth: 370, maxWidth: 440 }}>
              <div style={{ fontWeight: 700, color: 'var(--color-primary)', marginBottom: 12, fontSize: '1.18em', letterSpacing: '0.01em' }}>🛒 カート</div>
              <div style={{ marginBottom: 10, fontSize: '1.05em' }}>商品数: <b>{cartCount}</b></div>
              <div style={{ marginBottom: 18, fontWeight: 600, color: '#E1306C' }}>合計: <b>{totalPrice.toLocaleString()}円</b></div>
              {cart.length === 0 ? (
                <div style={{ color: '#888', textAlign: 'center', padding: '1.5em 0' }}>カートに商品がありません</div>
              ) : (
                <ul style={{ listStyle: 'none', padding: 0, margin: 0, maxHeight: 320, overflowY: 'auto' }}>
                  {cart.map(item => (
                    <li key={item.managementNumber} style={{ display: 'flex', alignItems: 'center', gap: '1em', borderBottom: '1px solid #FFD4C4', padding: '1em 0' }}>
                      <img src={getImageSrc(item.imageUrl)} alt={item.name} style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 10, border: '1px solid #FFD4C4', background: '#fafafa' }} />
                      <div style={{ flex: 1 }}>
                        <div style={{ fontWeight: 700, fontSize: '1.08em', marginBottom: 2 }}>{item.name}</div>
                        <div style={{ color: '#636E72', fontSize: '0.98em', marginBottom: 4 }}>単価: {Number(String(item.price).replace(/[^\d.]/g, '')).toLocaleString()}円</div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5em', marginTop: 4 }}>
                          <button onClick={() => updateQuantity(item.managementNumber, Math.max(1, item.quantity - 1))} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 28, height: 28, fontWeight: 700, cursor: 'pointer', fontSize: '1.1em', transition: 'background 0.2s' }}>-</button>
                          <span style={{ fontWeight: 600, minWidth: 36, textAlign: 'center', fontSize: '1.08em' }}>{item.quantity}</span>
                          <button onClick={() => updateQuantity(item.managementNumber, item.quantity + 1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 28, height: 28, fontWeight: 700, cursor: 'pointer', fontSize: '1.1em', transition: 'background 0.2s' }}>+</button>
                        </div>
                      </div>
                      <button onClick={() => removeFromCart(item.managementNumber)} style={{ background: '#ff6b6b', color: '#fff', border: 'none', borderRadius: 8, padding: '0.5em 1em', fontSize: '1em', cursor: 'pointer', marginLeft: 6, fontWeight: 700, transition: 'background 0.2s' }}>削除</button>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </div>
      </div>
      <nav style={{ background: '#f7f3ef', borderTop: '1px solid var(--color-border)', borderBottom: '1px solid var(--color-border)' }}>
        <ul className="header-nav-list" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '1.7em', margin: 0, padding: '0.35em 0', listStyle: 'none', fontSize: '0.98em' }}>
          {navItems.map(item => (
            <li key={item.to}>
              <Link
                to={item.to}
                style={{
                  color: location.pathname === item.to ? 'var(--color-primary)' : 'var(--color-text)',
                  textDecoration: 'none',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  gap: '0.15em',
                  fontWeight: location.pathname === item.to ? 700 : 500,
                }}
                className="header-nav-link"
              >
                <span className="header-nav-link-icon" style={{ fontSize: '1.13em' }}>{item.icon}</span>
                <span className="header-nav-link-label" style={{ fontSize: '0.92em' }}>{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
