import React, { useEffect, useState } from 'react';
// Firebase
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';

const firebaseConfig = {
  apiKey: "AIzaSyB2nPjePLfr6ZlxTOLPC7OuYWrD0k6JqJ4",
  authDomain: "link-manager-f4ea8.firebaseapp.com",
  projectId: "link-manager-f4ea8",
  storageBucket: "link-manager-f4ea8.firebasestorage.app",
  messagingSenderId: "96350140236",
  appId: "1:96350140236:web:fe858a8661067f5f0081e9",
  measurementId: "G-90CXT9WS4Y"
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

interface LinkData {
  id: string;
  title: string;
  price?: string;
  applyUrl?: string;
  influencerUrl?: string;
  imageUrl?: string;
}

interface CartItem {
  managementNumber: string;
  name: string;
  price: string;
  imageUrl?: string;
  quantity: number;
  length?: number;
}

const InfluencerCollab: React.FC = () => {
  const [links, setLinks] = useState<LinkData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [cart, setCart] = useState<CartItem[]>([]);
  const [cartModalOpen, setCartModalOpen] = useState(false);

  // Firestoreからコラボ商品リンク取得（admin_links.htmlと同じ構造）
  useEffect(() => {
    async function fetchLinks() {
      setLoading(true);
      setError(null);
      try {
        const linksCol = collection(db, 'links');
        const snapshot = await getDocs(linksCol);
        const linksList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as LinkData));
        setLinks(linksList);
      } catch (e: any) {
        setError('リンクの取得に失敗しました');
        console.error('Error fetching links:', e);
      } finally {
        setLoading(false);
      }
    }
    fetchLinks();
  }, []);

  // カートのローカルストレージ同期
  useEffect(() => {
    const savedCart = localStorage.getItem('mcSquareCart');
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch {
        setCart([]);
      }
    }
  }, []);
  useEffect(() => {
    localStorage.setItem('mcSquareCart', JSON.stringify(cart));
  }, [cart]);

  // カート操作
  const handleChangeQuantity = (managementNumber: string, diff: number) => {
    setCart(prev => prev.map(item =>
      item.managementNumber === managementNumber
        ? { ...item, quantity: Math.max(1, item.quantity + diff), length: Math.max(1, (item.quantity + diff) * 50) }
        : item
    ));
  };
  const handleRemove = (managementNumber: string) => {
    setCart(prev => prev.filter(item => item.managementNumber !== managementNumber));
  };
  const handleCheckout = () => {
    alert('決済処理（外部連携など）をここに実装します');
  };
  const cartCount = cart.reduce((sum, item) => sum + item.quantity, 0);
  const subtotal = cart.reduce((sum, item) => {
    const priceNum = Number(String(item.price).replace(/[^\d.]/g, ''));
    return sum + priceNum * item.quantity;
  }, 0);
  const totalLength = cart.reduce((sum, item) => sum + (item.length || 0), 0);
  const shippingFee = subtotal >= 24200 ? 0 : (totalLength <= 200 ? 400 : 900);
  const total = subtotal + shippingFee;

  // 画像パス正規化
  const getImageSrc = (url?: string) => {
    if (!url) return '/Image/MC square Logo.png';
    if (url.startsWith('http://') || url.startsWith('https://')) return url;
    if (url.startsWith('/Image/')) return url;
    if (url.startsWith('Image/')) return '/' + url;
    return '/Image/MC square Logo.png';
  };

  // カートモーダル
  const CartModal = () => (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', zIndex: 2000, display: 'flex', alignItems: 'center', justifyContent: 'center' }} onClick={() => setCartModalOpen(false)}>
      <div style={{ background: '#fff', borderRadius: 16, padding: '2em', minWidth: 320, maxWidth: 400, boxShadow: '0 8px 32px rgba(0,0,0,0.15)', position: 'relative' }} onClick={e => e.stopPropagation()}>
        <h2 style={{ fontSize: '1.2em', color: 'var(--color-primary)', marginBottom: '1em' }}>カートの中身</h2>
        {cart.length === 0 ? (
          <div style={{ color: '#888', textAlign: 'center', margin: '2em 0' }}>カートに商品がありません</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {cart.map(item => (
              <li key={item.managementNumber} style={{ display: 'flex', alignItems: 'center', gap: '1em', borderBottom: '1px solid #FFD4C4', padding: '0.7em 0' }}>
                <img src={getImageSrc(item.imageUrl)} alt={item.name} style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 8, border: '1px solid #FFD4C4' }} />
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 700 }}>{item.name}</div>
                  <div style={{ color: '#636E72', fontSize: '0.95em' }}>{item.price} × {item.quantity}枚 ({item.length || item.quantity * 50}cm)</div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5em', marginTop: 4 }}>
                    <button onClick={() => handleChangeQuantity(item.managementNumber, -1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>-</button>
                    <span style={{ fontWeight: 600, minWidth: 32, textAlign: 'center' }}>{item.quantity}枚</span>
                    <button onClick={() => handleChangeQuantity(item.managementNumber, 1)} style={{ background: '#f7f3ef', color: '#333', border: '1px solid #FFD4C4', borderRadius: '50%', width: 24, height: 24, fontWeight: 700, cursor: 'pointer' }}>+</button>
                  </div>
                </div>
                <button onClick={() => handleRemove(item.managementNumber)} style={{ background: '#ff6b6b', color: '#fff', border: 'none', borderRadius: 6, padding: '0.3em 0.6em', fontSize: '0.9em', cursor: 'pointer' }}>削除</button>
              </li>
            ))}
          </ul>
        )}
        <div style={{ marginTop: '1.5em', fontWeight: 700, fontSize: '1.1em', textAlign: 'right' }}>小計: {subtotal.toLocaleString()}円<br />送料: {shippingFee === 0 ? '送料無料' : shippingFee.toLocaleString() + '円'}<br /><span style={{ fontSize: '1.15em' }}>合計: {total.toLocaleString()}円</span></div>
        {cart.length > 0 && <button onClick={handleCheckout} style={{ marginTop: '1.5em', background: 'var(--color-primary)', color: '#fff', border: 'none', borderRadius: 8, padding: '0.9em 1.5em', fontWeight: 700, fontSize: '1.08em', cursor: 'pointer', display: 'block', width: '100%' }}>決済へ進む</button>}
        <button onClick={() => setCartModalOpen(false)} style={{ marginTop: '1em', background: '#ccc', color: '#333', border: 'none', borderRadius: 8, padding: '0.7em 1.5em', fontWeight: 700, fontSize: '1em', cursor: 'pointer', display: 'block', width: '100%' }}>閉じる</button>
      </div>
    </div>
  );

  return (
    <div className="container">
      <h1 className="section-title" style={{ color: 'var(--color-primary)', fontSize: '1.6rem', margin: '2rem 0 1rem 0' }}>インフルエンサーコラボ</h1>
      <div style={{ background: '#fff7f2', border: '2px solid var(--color-primary)', borderRadius: 12, padding: '1.5rem', margin: '2rem auto', maxWidth: 600, textAlign: 'center' }}>
        <p style={{ color: 'var(--color-primary)', fontWeight: 600, fontSize: '1.1rem', marginBottom: '0.5rem' }}>⚠️ ご注意</p>
        <p style={{ color: 'var(--color-text)', fontSize: '1rem', margin: 0 }}>インフルエンサーコラボ商品は外部サイト（BASE）でのご購入となります。<br />下記の「申し込みページへ」ボタンから外部サイトに移動してご購入ください。</p>
      </div>
      <div className="links-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '2rem', margin: '2rem 0' }}>
        {loading ? (
          <div style={{ textAlign: 'center', color: '#aaa' }}>読み込み中...</div>
        ) : error ? (
          <div style={{ textAlign: 'center', color: '#f00' }}>{error}</div>
        ) : links.length === 0 ? (
          <div style={{ textAlign: 'center', color: '#aaa' }}>リンクがありません</div>
        ) : (
          links.map((link) => (
            <div className="link-card" key={link.id} style={{ background: '#fff', borderRadius: 20, boxShadow: '0 4px 16px rgba(255, 159, 124, 0.10)', border: '1px solid #FFD4C4', padding: '1.5rem', position: 'relative', overflow: 'hidden', textAlign: 'left' }}>
              <div className="link-title" style={{ fontSize: '1.3rem', fontWeight: 'bold', color: 'var(--color-primary)', marginBottom: '0.7rem' }}>{link.title}</div>
              <div className="link-price" style={{ color: 'var(--color-text)', marginBottom: '0.7rem', fontSize: '1.1rem' }}><b>値段:</b> {link.price || ''}</div>
              <div className="link-buttons" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem', flexWrap: 'wrap' }}>
                {link.applyUrl && <a href={link.applyUrl} target="_blank" rel="noopener" className="link-btn" style={{ background: 'var(--color-primary)', color: '#fff', textDecoration: 'none', padding: '0.6rem 1.3rem', borderRadius: 20, fontWeight: 500, fontSize: '1rem', boxShadow: '0 2px 8px rgba(255, 159, 124, 0.08)' }}>申し込みページへ</a>}
                {link.influencerUrl && <a href={link.influencerUrl} target="_blank" rel="noopener" className="link-btn instagram-btn" style={{ background: '#fff', color: 'var(--color-primary)', border: '2px solid var(--color-primary)', fontWeight: 700, padding: '0.6rem 1.3rem', borderRadius: 20, fontSize: '1rem' }}>インフルエンサーを見る</a>}
              </div>
              {link.imageUrl && <img src={getImageSrc(link.imageUrl)} alt="画像" style={{ maxWidth: 100, maxHeight: 100, borderRadius: 12, marginTop: '0.5rem' }} />}
            </div>
          ))
        )}
      </div>
      <div className="contact" style={{ background: 'var(--color-background-alt)', borderRadius: 20, padding: '2rem', marginTop: '2rem', boxShadow: '0 4px 8px rgba(255, 159, 124, 0.08)', border: '1px solid #FFD4C4', maxWidth: 700, marginLeft: 'auto', marginRight: 'auto', position: 'relative', overflow: 'hidden' }}>
        <strong>【お問合せ先】</strong><br />
        〒244-0811 神奈川県横浜市戸塚区上柏尾町132-3 SKビル2階<br />
        TEL：045-410-7023　FAXフリーダイヤル：0120-535-596<br />
        E-mail：<a href="mailto:retail@mcsquareofficials.com" style={{ color: 'var(--color-primary)', textDecoration: 'none' }}>retail@mcsquareofficials.com</a>
      </div>
    </div>
  );
};

export default InfluencerCollab;
