import React, { useEffect, useState } from 'react';
import { getAuth, deleteUser } from 'firebase/auth';
import { getFirestore, doc, getDoc, setDoc, updateDoc, deleteDoc } from 'firebase/firestore';
import { app } from './firebase';
import { useNavigate } from 'react-router-dom';

const db = getFirestore(app);
const auth = getAuth(app);

const MyPage: React.FC = () => {
  const [userData, setUserData] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [registerName, setRegisterName] = useState('');
  const [registerMembership, setRegisterMembership] = useState('');
  const [showWithdrawalConfirm, setShowWithdrawalConfirm] = useState(false);
  const [editingMembership, setEditingMembership] = useState(false);
  const [newMembershipNumber, setNewMembershipNumber] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      setError(null);
      try {
        const user = auth.currentUser;
        if (!user) {
          setError('ログインしてください');
          setLoading(false);
          return;
        }
        const userRef = doc(db, 'users', user.uid);
        const userSnap = await getDoc(userRef);
        if (userSnap.exists()) {
          setUserData(userSnap.data());
          setShowRegisterForm(false);
        } else {
          setShowRegisterForm(true);
        }
      } catch (e: any) {
        setError('データ取得エラー: ' + e.message);
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, []);

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    try {
      const user = auth.currentUser;
      if (!user) {
        setError('ログインしてください');
        setLoading(false);
        return;
      }
      const userRef = doc(db, 'users', user.uid);
      await setDoc(userRef, {
        name: registerName,
        email: user.email,
        membershipNumber: registerMembership,
        point: 0,
        orders: []
      });
      setUserData({
        name: registerName,
        email: user.email,
        membershipNumber: registerMembership,
        point: 0,
        orders: []
      });
      setShowRegisterForm(false);
    } catch (e: any) {
      setError('登録エラー: ' + e.message);
    } finally {
      setLoading(false);
    }
  };

  const handleWithdrawal = async () => {
    setError(null);
    setLoading(true);
    try {
      const user = auth.currentUser;
      if (!user) {
        setError('ログインしてください');
        setLoading(false);
        return;
      }

      // Firestoreのユーザーデータを削除
      const userRef = doc(db, 'users', user.uid);
      await deleteDoc(userRef);

      // Firebase Authenticationのアカウントを削除
      await deleteUser(user);

      // ホームページにリダイレクト
      navigate('/');
    } catch (e: any) {
      setError('退会処理でエラーが発生しました: ' + e.message);
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await auth.signOut();
      navigate('/');
    } catch (e: any) {
      setError('ログアウトでエラーが発生しました: ' + e.message);
    }
  };

  // 会員番号編集保存処理
  const handleSaveMembershipNumber = async () => {
    setError(null);
    setLoading(true);
    try {
      const user = auth.currentUser;
      if (!user) {
        setError('ログインしてください');
        setLoading(false);
        return;
      }
      const userRef = doc(db, 'users', user.uid);
      await updateDoc(userRef, { membershipNumber: newMembershipNumber });
      setUserData({ ...userData, membershipNumber: newMembershipNumber });
      setEditingMembership(false);
    } catch (e: any) {
      setError('会員番号の更新でエラーが発生しました: ' + e.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div style={{ padding: '2em' }}>読み込み中...</div>;
  if (error) return <div style={{ color: 'red', padding: '2em' }}>{error}</div>;

  if (showRegisterForm) {
    return (
      <div style={{ maxWidth: 500, margin: '2em auto', background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(255,159,124,0.10)', padding: '2em' }}>
        <h2 style={{ color: 'var(--color-primary)', marginBottom: 24 }}>マイページ初回登録</h2>
        <form onSubmit={handleRegister}>
          <div style={{ marginBottom: 16 }}>
            <label>名前：</label><br />
            <input type="text" value={registerName} onChange={e => setRegisterName(e.target.value)} required style={{ width: '100%', padding: '0.7em', borderRadius: 8, border: '1px solid #ccc' }} />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>会員番号：</label><br />
            <input type="text" value={registerMembership} onChange={e => setRegisterMembership(e.target.value)} style={{ width: '100%', padding: '0.7em', borderRadius: 8, border: '1px solid #ccc' }} />
            <div style={{ fontSize: '0.95em', color: '#888', marginTop: 4 }}>※ 会員番号が分からない場合は空欄のまま登録できます</div>
          </div>
          <button type="submit" style={{ width: '100%', padding: '0.8em', borderRadius: 8, background: 'var(--color-primary)', color: '#fff', border: 'none', fontWeight: 700, fontSize: '1.08em' }}>登録</button>
        </form>
      </div>
    );
  }

  if (!userData) return null;

  return (
    <div style={{ maxWidth: 500, margin: '2em auto', background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(255,159,124,0.10)', padding: '2em' }}>
      <h2 style={{ color: 'var(--color-primary)', marginBottom: 24 }}>マイページ</h2>
      <div style={{ marginBottom: 16 }}><b>名前:</b> {userData.name || '-'}</div>
      <div style={{ marginBottom: 16 }}><b>メールアドレス:</b> {userData.email || '-'}</div>
      <div style={{ marginBottom: 16 }}>
        <b>会員番号:</b>{' '}
        {editingMembership ? (
          <>
            <input
              type="text"
              value={newMembershipNumber}
              onChange={e => setNewMembershipNumber(e.target.value)}
              style={{ padding: '0.4em', borderRadius: 6, border: '1px solid #ccc', marginRight: 8, width: 140 }}
              placeholder="会員番号を入力"
            />
            <button
              onClick={handleSaveMembershipNumber}
              disabled={loading}
              style={{ padding: '0.4em 1em', borderRadius: 6, background: 'var(--color-primary)', color: '#fff', border: 'none', fontWeight: 600, marginRight: 6 }}
            >保存</button>
            <button
              onClick={() => setEditingMembership(false)}
              style={{ padding: '0.4em 1em', borderRadius: 6, background: '#eee', color: '#666', border: 'none', fontWeight: 600 }}
            >キャンセル</button>
          </>
        ) : (
          <>
            {userData.membershipNumber || '-'}
            <button
              onClick={() => { setEditingMembership(true); setNewMembershipNumber(userData.membershipNumber || ''); }}
              style={{ marginLeft: 12, padding: '0.3em 0.9em', borderRadius: 6, background: '#f7f7f7', color: '#666', border: '1px solid #ccc', fontWeight: 600, fontSize: '0.95em', cursor: 'pointer' }}
            >編集</button>
          </>
        )}
      </div>
      <div style={{ marginBottom: 16 }}><b>現在のポイント:</b> {userData.point !== undefined ? userData.point : '-'} pt</div>
      <div style={{ marginBottom: 24 }}>
        <b>購入履歴:</b>
        <ul style={{ margin: 0, paddingLeft: 20 }}>
          {userData.orders && userData.orders.length > 0 ? (
            userData.orders.map((order: any, idx: number) => (
              <li key={idx}>{order}</li>
            ))
          ) : (
            <li>購入履歴はありません</li>
          )}
        </ul>
      </div>
      
      <div style={{ display: 'flex', gap: 12, marginTop: 24 }}>
        <button 
          onClick={handleLogout}
          style={{ 
            flex: 1,
            padding: '0.8em', 
            borderRadius: 8, 
            background: 'var(--color-primary)', 
            color: '#fff', 
            border: 'none', 
            fontWeight: 700, 
            fontSize: '1.08em',
            cursor: 'pointer'
          }}
        >
          ログアウト
        </button>
        <button 
          onClick={() => setShowWithdrawalConfirm(true)}
          style={{ 
            padding: '0.8em 1.2em', 
            borderRadius: 8, 
            background: '#fff', 
            color: '#999', 
            border: '1px solid #ddd', 
            fontWeight: 600, 
            fontSize: '0.95em',
            cursor: 'pointer'
          }}
        >
          退会
        </button>
      </div>
      
      {showWithdrawalConfirm ? (
        <div style={{ marginTop: 16, padding: 16, border: '2px solid #ff6b6b', borderRadius: 8, background: '#fff5f5' }}>
          <h3 style={{ color: '#ff6b6b', marginBottom: 12 }}>退会の確認</h3>
          <p style={{ marginBottom: 16, color: '#666' }}>
            退会すると、アカウントとすべてのデータが完全に削除されます。<br />
            この操作は取り消すことができません。
          </p>
          <div style={{ display: 'flex', gap: 12 }}>
            <button 
              onClick={handleWithdrawal}
              disabled={loading}
              style={{ 
                padding: '0.8em 1.5em', 
                borderRadius: 8, 
                background: '#ff6b6b', 
                color: '#fff', 
                border: 'none', 
                fontWeight: 700,
                cursor: loading ? 'not-allowed' : 'pointer',
                opacity: loading ? 0.6 : 1
              }}
            >
              {loading ? '処理中...' : '退会する'}
            </button>
            <button 
              onClick={() => setShowWithdrawalConfirm(false)}
              style={{ 
                padding: '0.8em 1.5em', 
                borderRadius: 8, 
                background: '#f8f9fa', 
                color: '#666', 
                border: '1px solid #ddd', 
                fontWeight: 700,
                cursor: 'pointer'
              }}
            >
              キャンセル
            </button>
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default MyPage;
