import React, { useEffect, useState } from 'react';

// 商品型定義
interface Product {
  managementNumber: string;
  name: string;
  price: string;
  imageUrl?: string;
  status?: string;
}

const GAS_WEB_APP_URL = 'https://script.google.com/macros/s/AKfycbygEEOmylE1fzaMtpxAReEQfY02zIcUVKwVPaV4R5H5AKWnQtgnUbYOKfq3y4mYJPdzYg/exec';

const Subscription: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchProducts() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(GAS_WEB_APP_URL, { method: 'GET', mode: 'cors', headers: { 'Accept': 'application/json' } });
        if (!response.ok) throw new Error(`データの取得に失敗しました (${response.status})`);
        const data = await response.json();
        if (!Array.isArray(data)) throw new Error('データの形式が不正です');
        // サブスク商品だけを抽出（例: nameに"サブスク"や"subscription"が含まれるもの）
        setProducts(data.filter((item: any) => item.status === '公開中' && (item.name?.includes('サブスク') || item.name?.toLowerCase().includes('subscription'))));
      } catch (e: any) {
        setError(e.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProducts();
  }, []);

  return (
    <div className="container">
      <h1 style={{ color: 'var(--color-primary)', fontSize: '1.6rem', marginBottom: '0.5rem' }}>サブスクリプション商品一覧</h1>
      {loading && <div>データを読み込み中...</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <div className="inventory-list" style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem', justifyContent: 'center', marginTop: '2rem' }}>
        {products.map(product => (
          <div key={product.managementNumber} className="inventory-card" style={{ background: '#fff', borderRadius: 20, boxShadow: '0 4px 16px rgba(255, 159, 124, 0.10)', border: '1px solid #FFD4C4', width: 320, padding: '1.5rem', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            {product.imageUrl && <img src={product.imageUrl} alt={product.managementNumber} style={{ width: 160, height: 160, objectFit: 'cover', borderRadius: 16, border: '1px solid #FFD4C4', marginBottom: '1rem', background: '#f8f8f8' }} />}
            <div className="product-name">{product.name}</div>
            <div className="managementNumber">管理番号: {product.managementNumber}</div>
            <div className="price">{product.price}</div>
            <div className="status">{product.status}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Subscription;
